import './SidebarUserProfile.css';
import { styled } from 'styled-components';
import { ReactComponent as StarIcon } from '../../../assets/icons/StarIcon.svg';
import defaultUserAvatar from '../../../assets/img/userAvatar.png';

const UserProfileWrapper = styled.div`
  display: flex;
  align-items: center;
  padding: 1rem;
  margin-bottom: 1.875rem;
  border-radius: 1.25rem;
  background: ${({ $isActive }) => $isActive ? '#E4CFFF' : 'white'}; /* Светло-фиолетовый для активного состояния */
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${({ $isActive }) => $isActive ? '#D8C2FF' : '#F0F0F0'}; /* Затемнение при наведении */
  }
`;

const UserInfo = styled.div`
  h3 {
    margin: 0;
    font-size: 16px;
    font-weight: bold;
    color: #333;
  }
  p {
    margin: 4px 0 0 0;
    font-size: 12px;
    color: #667085;
  }
`;

const UserStats = styled.div`
  display: flex;
  align-items: center;
  margin-top: 4px;
  font-size: 12px;
`;

const XpDisplay = styled.span`
  color: #FFC711;
  font-weight: bold;
  margin-left: 0.25rem;
`;

const StarIconPlaceholder = styled.div`
  width: 1.2rem;
  height: 1.2rem;
  color: #FFC711;

  svg {
    display: block;
    width: 100%;
    height: 100%;
    fill: currentColor;
  }
`;

export default function SidebarUserProfile({ user, isActive, onClick }) {
  if (!user) {
    return <UserProfileWrapper 
      data-testid="user-profile" 
      data-active="false"
      $isActive={false} 
      onClick={() => {}}
    >
      Загрузка...
    </UserProfileWrapper>;
  }

  return (
    <UserProfileWrapper 
      data-testid="user-profile"
      data-active={isActive.toString()}
      $isActive={isActive} 
      onClick={onClick}
    >
      <div className="avatar">
        <img src={user.avatar || defaultUserAvatar} alt="User Avatar" />
      </div>
      <UserInfo>
        <h3>{user.name}</h3>
        <p>{user.level} уровень</p>
        <UserStats>
          <StarIconPlaceholder>
            <StarIcon />
          </StarIconPlaceholder>
          <XpDisplay>{user.xp || 0}</XpDisplay>
        </UserStats>
      </UserInfo>
    </UserProfileWrapper>
  );
}

import './SidebarButton.css'

import { ReactComponent as FilterIcon } from '../../../assets/icons/FilterIcon19.svg'
import { ReactComponent as PlusIcon } from '../../../assets/icons/PlusIcon19.svg'



export default function SidebarButton({ children, isActive, onClick, buttonType }) {
  return (
    <button
      onClick={ onClick }
      className={
        buttonType === 'addTask' ? (
          isActive ? 'button active' : 'button addTask'
        ) : (
          isActive ? 'button active' : 'button'
        )
      }
    >
      { buttonType === 'addTask' ? <PlusIcon /> : <FilterIcon /> }
      { children }
    </button>
  )
}

import { styled } from 'styled-components';
import SidebarButton from '../SidebarButton/SidebarButton';

const SidebarMenuContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem; // Added some gap between buttons
`;

export default function SidebarMenu({ active, onChange, menuItems }) {
  return (
    <SidebarMenuContainer>
      {menuItems.map((item, index) => (
        <SidebarButton
          isActive={active === item}
          key={index}
          onClick={() => onChange(item)}
          buttonType={item === 'Добавить задачу' ? 'addTask' : 'menuItem'}
        >
          {item}
        </SidebarButton>
      ))}
    </SidebarMenuContainer>
  );
}

import { styled } from 'styled-components';
import SidebarUserProfile from './SidebarUserProfile/SidebarUserProfile';
import SidebarMenu from './SidebarMenu/SidebarMenu';
import { useUser } from '../../contexts/UserContext';

const SidebarContainer = styled.nav`
  width: 25%;
  background: #F7F1FF; /* Базовый цвет сайдбара */
  padding: 1.25rem;
  overflow-y: auto;
`;

export default function Sidebar({ activeMenuItem, onMenuItemChange, onProfileClick, menuItems }) {
  const { user } = useUser();

  return (
    <SidebarContainer>
      <SidebarUserProfile
        user={user}
        isActive={activeMenuItem === 'Профиль'}
        onClick={onProfileClick}
      />
      <SidebarMenu
        active={activeMenuItem}
        onChange={onMenuItemChange}
        menuItems={menuItems}
      />
    </SidebarContainer>
  );
}
